ST.cache = (function () {
    var me = {},
        _data = { },
        _idSeed = 0,
        _logger = ST.logger.forClass('ST.cache');

    me.add = function (obj) {
        _logger.trace('.add', obj);
        var id = ++_idSeed;
        _data[id] = obj;
        return id;

    }

    me.get = function (id) {
        _logger.trace('.get', id);
        if (typeof id === 'number') {
            return _data[id];
        } else {
            _logger.error('Could not retrieve cached object with non-numeric id ' + id);
        }
    }

    return me;
}());
