(function () {
    var _enabled = false;

    var debug = function (arg) {
        if (_enabled) {
            if (typeof arg === 'undefined') {
                debugger; // eslint-disable-line
            } else if (typeof arg === 'function') {
                arg();
            }
        }
    }

    debug.enable = function () {
        _enabled = true;
    }

    debug.disable = function () {
        _enabled = false;
    }

    debug.isEnabled = function () {
        return _enabled;
    }

    ST.debug = debug;
}());