"use strict";

describe('locate', () => {
    
    const { createDom } = require('../../../helper/jsdom-util');
    
    describe('atPaths', () => {
        it('should return @ for null el or body el', () => {
            var dom = createDom('<body></body>'),
                window = dom.window,
                _ST = window.ST,
                body = window.document.querySelector('body'),
                strategy = new _ST.locator.Strategy(),
                targets = [];
            
            strategy.locate(null, targets);
            expect(targets).toEqual([
                [body, '@'],
                [body, '//body']
            ]);

            targets = [];
            strategy.locate(body, targets);
            expect(targets).toEqual([
                [body, '@'],
                [body, '//body']
            ]);
        });
    });
    
});
